INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (156, 'ZP', 660, NULL, 143, '2005/2006', 'Vyhodnocovanie vedomost tudenta pri tdiu prostrednctvom webovho adaptvneho systmu', 'Vyhodnocovanie vedomost tudenta pri tdiu prostrednctvom webovho adaptvneho systmu', 'Webov adaptvne vubov systmy umouj dosiahnutie vyej efektvnosti uenia sa tm, e vubov systm sa doke prispsobi jednotlivm tudentom, resp. skupinm tudentov. Pri prispsobovan hr vznamn lohu prve schopnos systmu vyhodnoti charakteristiky pouvatea vzhadom na jeho predchdzajcu innos vo vubovom systme. Skmajte monosti vyhodnotenia charakteristk pouvatea vzhadom na prezentovan vedomosti (ktor asti len videl, ktor pochopil). Zamerajte sa na systmy na vubu programovania. Zoberte pri tom do vahy pohyb pouvatea v informanom priestore, vyhodnotenie otzok pouvateovi po pretudovan jednotlivch ast a tie monos vyhodnotenia prkladov programov, ktor tudent pri prci so systmom vyrieil. Navrhnite spsob ako tieto charakteristiky vyhodnocova v kontexte vuby programovania (vo zvolenej paradigme: procedurlna, objektovo-orientovan, funkcionlna alebo logick). Nvrh experimentlne overte tak, e doplnte a obohatte existujci otvoren systm AHA! pre vubu programovania (systm AHA! je vyvinut modernmi technolgiami  Java servlety, Tomcat web server, XML a alie a je dostupn na adrese http://aha.win.tue.nl).', 'Webov adaptvne vubov systmy umouj dosiahnutie vyej efektvnosti uenia sa tm, e vubov systm sa doke prispsobi jednotlivm tudentom, resp. skupinm tudentov. Pri prispsobovan hr vznamn lohu prve schopnos systmu vyhodnoti charakteristiky pouvatea vzhadom na jeho predchdzajcu innos vo vubovom systme. Skmajte monosti vyhodnotenia charakteristk pouvatea vzhadom na prezentovan vedomosti (ktor asti len videl, ktor pochopil). Zamerajte sa na systmy na vubu programovania. Zoberte pri tom do vahy pohyb pouvatea v informanom priestore, vyhodnotenie otzok pouvateovi po pretudovan jednotlivch ast a tie monos vyhodnotenia prkladov programov, ktor tudent pri prci so systmom vyrieil. Navrhnite spsob ako tieto charakteristiky vyhodnocova v kontexte vuby programovania (vo zvolenej paradigme: procedurlna, objektovo-orientovan, funkcionlna alebo logick). Nvrh experimentlne overte tak, e doplnte a obohatte existujci otvoren systm AHA! pre vubu programovania (systm AHA! je vyvinut modernmi technolgiami  Java servlety, Tomcat web server, XML a alie a je dostupn na adrese http://aha.win.tue.nl).', 1);
