INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (290, 'ZP', 10, NULL, 266, '2005/2006', 'Simulovan robotick futbal - vyie schopnosti hrov', 'Simulovan robotick futbal - vyie schopnosti hrov', 'Simulovan robotick futbal sa zaober vytvranm tmov spolupracujcich agentov, ktor v simulovanom prostred navzjom speria. Jednotliv hri-agenti predstavuj samostatn programy, ktor prostrednctvom siete komunikuj so simulanm serverom. Venujte sa vym schopnostiam hrov. Navrhnite a implementujte niektor z vych schopnost hra, prpadne vylepite niektor z existujcich prstupov.', 'Simulovan robotick futbal sa zaober vytvranm tmov spolupracujcich agentov, ktor v simulovanom prostred navzjom speria. Jednotliv hri-agenti predstavuj samostatn programy, ktor prostrednctvom siete komunikuj so simulanm serverom. Venujte sa vym schopnostiam hrov. Navrhnite a implementujte niektor z vych schopnost hra, prpadne vylepite niektor z existujcich prstupov.', 1);
