INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (295, 'ZP', 655, NULL, 273, '2005/2006', 'Zisovanie charakteristk pripojenia v rmci SR na zklade IP adresy', 'Zisovanie charakteristk pripojenia v rmci SR na zklade IP adresy', 'Analyzujte monosti vytvorenia aplikcie, ktor by umonila na zklade IP adresy pouvatea identifikova charakteristick informcie o jeho pripojen. Medzi tie patr naprklad regin (mesto), poskytovate pripojenia, typ pripojenia (pevn linka, modem, bezdrtov pripojenie, wifi,...) a pod. Na vytvorenie databzy mete poui naprklad databzu RIPE, informciu o reverznom DNS zzname pre dan IP adresu, alebo alie verejne dostupn informcie.', 'Analyzujte monosti vytvorenia aplikcie, ktor by umonila na zklade IP adresy pouvatea identifikova charakteristick informcie o jeho pripojen. Medzi tie patr naprklad regin (mesto), poskytovate pripojenia, typ pripojenia (pevn linka, modem, bezdrtov pripojenie, wifi,...) a pod. Na vytvorenie databzy mete poui naprklad databzu RIPE, informciu o reverznom DNS zzname pre dan IP adresu, alebo alie verejne dostupn informcie.', 1);
