INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (363, 'ZP', 35, NULL, 349, '2005/2006', 'Interpret lambda-vrazov', 'Interpret lambda-vrazov', 'Vaou lohou bude natudova lambda-kalkulus a vytvori program, ktor bude schopn interpretova lambda-vrazy. Z uritho pohadu meme lambda-kalkulus povaova za programovac jazyk. Jedn sa o pln programovac jazykumouje nm opsa ubovon vpoet. Jedn sa (z pohadu smantiky) o jednoduch programovac jazyk. Je ho mon kompletne opsa na niekoko mlo stranch. Vaka tejto jednoduchosti bude mon pomerne ahko vytvori prslun interpret schopn interpretova programy napsan v tomto jazykulambda-vrazy. 

