INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (602, 'ZP', 359, NULL, 552, '2004/2005', 'Monitorovac panel do PC', 'Monitoring panel for PC', 'Navrhnite a zostrojte hardwarov monitorovac panel pre osobn pota/server. Jeho lohou je kontrola sprvnej innosti a stavu PC a jeho ovldanie. Pod kontrolou a ovldanm sa mysl monitorovanie teploty komponentov PC (aspo 4 teplotn senzory), monitorovanie a riadenie otok ventiltorov, monitorovanie inch snmaov (otvorenie case potaa, vpadok napjania), riadenie signlu reset a power on/off. Takto hw monitor bude komunikova s pouvateom prostrednctvom displaya a jednoduchej klvesnice. Konfigurovanie a tanie aktulneho stavu bude mon aj priamo z PC cez sriov port (resp. USB) (naprogramovan ako PnP zariadenie s vlastnm driverom). Podpora OS Windows poprpade UNIX/Linux. Hw monitor bude obsahova signalizan sirnu na indikciu poruchovho stavu.', 'Navrhnite a zostrojte hardwarov monitorovac panel pre osobn pota/server. Jeho lohou je kontrola sprvnej innosti a stavu PC a jeho ovldanie. Pod kontrolou a ovldanm sa mysl monitorovanie teploty komponentov PC (aspo 4 teplotn senzory), monitorovanie a riadenie otok ventiltorov, monitorovanie inch snmaov (otvorenie case potaa, vpadok napjania), riadenie signlu reset a power on/off. Takto hw monitor bude komunikova s pouvateom prostrednctvom displaya a jednoduchej klvesnice. Konfigurovanie a tanie aktulneho stavu bude mon aj priamo z PC cez sriov port (resp. USB) (naprogramovan ako PnP zariadenie s vlastnm driverom). Podpora OS Windows poprpade UNIX/Linux. Hw monitor bude obsahova signalizan sirnu na indikciu poruchovho stavu.', 0);
