INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (647, 'ZP', 566, NULL, 593, '2004/2005', 'Pouitie genetickho algoritmu k optimalizcii funkci', 'Use of genetic algorithm for optimalization', 'Geneticky algoritmus patri v sasnosti medzi efektivne globlne optimalizan prostriedky, ktor je schopn poskytn s vekou pravdepodobnosou globlne minimum n-rozmernej funkcii. Rieenie problmu (napr. premenn funkcie) s reprezentovan v binrnom kde. Pri zahjen algoritmu je vygenerovan populcia rieen, priom kad rieenie je ohodnotene "fitnes", ktor odra kvalitu rieenia. V rmci reproduknho procesu sa vyberaj tie rieenia, ktor maj vysok fitnes, priom pri reproduknom procese sa pouvaj dva genetick opertory: mutcia a krenie. V priebehu opakovanho reproduknho procesu v populcii vznikaj rieenia, ktor maj vysok fitnes, t.j. s priraden kvalitnmu rieeniu optimalizanho problmu. Prca bude spova vo vypracovan teoretickho popisu metdy a taktie aj v jednoduchel implementcii programu bu v Pascale alebo v C.
