INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (699, 'ZP', 433, NULL, 627, '2005/2006', 'Ohodnocovanie softvrovch systmov', 'Ohodnocovanie softvrovch systmov', 'Softvrov ininiersvo neustle had nov spsoby merania kvality softvrovch systmov. Medzi zkladn sledovan charakteristiky partia zviazanos a sdrnos. Modelujte zdrojov kd softvrovho systmu ako graf, ktorho uzly predstavuj entity objektovo-orientovanej realizcie systmu, a ktorho hrany reprezentuj vzby medzi tmito entitami. Vytvorte nstroj, ktor na zklade analzy rozsiahleho softvrovho systmu zmeria takto reprezentciu. Pomocou tohto nstroja vyhodnote niekoko rozsiahlejch systmov s vone dostupnm zdrojovm kdom.', 'Softvrov ininiersvo neustle had nov spsoby merania kvality softvrovch systmov. Medzi zkladn sledovan charakteristiky partia zviazanos a sdrnos. Modelujte zdrojov kd softvrovho systmu ako graf, ktorho uzly predstavuj entity objektovo-orientovanej realizcie systmu, a ktorho hrany reprezentuj vzby medzi tmito entitami. Vytvorte nstroj, ktor na zklade analzy rozsiahleho softvrovho systmu zmeria takto reprezentciu. Pomocou tohto nstroja vyhodnote niekoko rozsiahlejch systmov s vone dostupnm zdrojovm kdom.', 1);
INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (700, 'ZP', 294, NULL, 628, '2003/2004', 'Potaom generovan holografia', 'Potaom generovan holografia', 'Urobte reer publikci, natudujte a urobte analzu monost vpotov potaom generovanch hologramov (CGH) znaka premietan laserom pre aplikcie v obohatenej skutonosti (augmented reality). Vytvorte program pre vpoet a overte algoritmus vygenerovanm dt digitlneho hologramu jednoduchho geometrickho vzoru vo formte elektrnovho litografiu (DWG). Pojednajte o monostiach urchlenia vpotov CGH pomocou viacerch  potaov sasne.', 'Urobte reer publikci, natudujte a urobte analzu monost vpotov potaom generovanch hologramov (CGH) znaka premietan laserom pre aplikcie v obohatenej skutonosti (augmented reality). Vytvorte program pre vpoet a overte algoritmus vygenerovanm dt digitlneho hologramu jednoduchho geometrickho vzoru vo formte elektrnovho litografiu (DWG). Pojednajte o monostiach urchlenia vpotov CGH pomocou viacerch  potaov sasne.', 1);
INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (701, 'ZP', 27, NULL, 629, '2004/2005', 'Vvojov prostredie pre algoritmy systmov sborov', 'Integrated Development Environment for Filesystem Algorithms', 'Analyzujte dostupn otvoren modely systmov sborov, z hadiska ich pouitenosti ako vvojovho a testovacieho prostredia pre nov implementan metdy v architektrach operanch systmov. Navrhnite a realizujte vvojov a testovacie prostredie, zameran na systmy sborov. V tomto prostred demontrujte prklady vytvorench a testovanch algoritmov.', 'Analyzujte dostupn otvoren modely systmov sborov, z hadiska ich pouitenosti ako vvojovho a testovacieho prostredia pre nov implementan metdy v architektrach operanch systmov. Navrhnite a realizujte vvojov a testovacie prostredie, zameran na systmy sborov. V tomto prostred demontrujte prklady vytvorench a testovanch algoritmov.', 1);
