INSERT INTO projects (id, project_type_id, academic_id, master_academic_id, graduate_id, study_year, name, name_en, description, description_en, specialization_id) VALUES (752, 'ZP', 437, NULL, 664, '2003/2004', 'Softvr ako vsledok technickej tvorivej duevnej innosti', 'Software as the result of technical creative mentation', 'Uvete pojem softvr z pohadu technickho aj prvneho, priom uvaujte nielen slovensk prvny systm, ale aj inonrodn a medzinrodn prvne pravy. Vysvetlite, ak s prva na softvr, o je to ochrana takchto prv, preo treba takto prva chrni s drazom na etick a spoloensk svislosti. Opte znme prvne rieenia ochrany softvru, priom op uvaujte nielen slovensk prvny systm, ale aj inonrodn a medzinrodn prvne pravy. Analyzujte sasn stav, pomenujte prpadn otvoren problmy a naznate monosti ich rieenia. O celej problematike vytvorte tmatick pavuinov sdlo, ktor bude sli ako zdroj informci aj odkazov na ne. ', 'Uvete pojem softvr z pohadu technickho aj prvneho, priom uvaujte nielen slovensk prvny systm, ale aj inonrodn a medzinrodn prvne pravy. Vysvetlite, ak s prva na softvr, o je to ochrana takchto prv, preo treba takto prva chrni s drazom na etick a spoloensk svislosti. Opte znme prvne rieenia ochrany softvru, priom op uvaujte nielen slovensk prvny systm, ale aj inonrodn a medzinrodn prvne pravy. Analyzujte sasn stav, pomenujte prpadn otvoren problmy a naznate monosti ich rieenia. O celej problematike vytvorte tmatick pavuinov sdlo, ktor bude sli ako zdroj informci aj odkazov na ne. ', 1);
