--
-- users
--
CREATE TABLE users
(
  id serial NOT NULL,
  personal_number varchar(32),
  username character varying(255) NOT NULL,
  ldapname character varying(255) NULL,
  "password" character varying(32) NOT NULL,
  first_name character varying(255) NOT NULL,
  middle_name character varying(255),
  last_name character varying(255) NOT NULL,
  title character varying(64),
  email character varying(255),
  address text,
  phone character varying(64),
  icq character varying(54),
  occupation character varying(255),
  forward_mail character varying(255), -- Ak zadane, tak na tuto adresu sa budu preposielat spravy zaslane pouzivatelovi.
  mail_listing int4 NOT NULL DEFAULT 20, -- listovanie lokalnych mailov
  publish_email int2 DEFAULT 0,	-- ci ma byt zverejneny email
  CONSTRAINT users_pkey PRIMARY KEY (id),
  CONSTRAINT users_personal_number_key UNIQUE (personal_number),
  CONSTRAINT users_username_key UNIQUE (username)
);

--
-- academics
-- 
-- zoznam academicov na skole
-- 
