--
-- users_clearances
--
-- custom/vlastne definovane opravnenia pre uzivatelov
-- su pouzivane napr. ak len 1 uzivatelovi chceme pridat 1 pravo, tak aby sme nemuseli 
-- vytvarat osobitnu rolu.
--
-- stlpec access urcuje ci bude dana rola odobrata alebo pridana z prav uzivatela.
-- tieto opravnenia sa aplikuju na opravnenia, ktore vzniknu spojenim roli, ktore pouzivatel vlastni.
-- 
CREATE TABLE users_clearances
(
  id serial NOT NULL,
  user_id integer NOT NULL,
  clearance_id integer NOT NULL,
  "access" character varying(5) NOT NULL DEFAULT 'allow'::character varying,
  CONSTRAINT users_clearances_pkey PRIMARY KEY (id),
  CONSTRAINT users_clearances_clearance_id_fkey FOREIGN KEY (clearance_id)
      REFERENCES clearances (id) MATCH SIMPLE
      ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT users_clearances_user_id_fkey FOREIGN KEY (user_id)
      REFERENCES users (id) MATCH SIMPLE
      ON UPDATE NO ACTION ON DELETE NO ACTION,
  CONSTRAINT users_clearances_user_id_key UNIQUE (user_id, clearance_id),
  CONSTRAINT users_clearances_access_check CHECK ("access"::text = ANY (ARRAY['allow'::character varying, 'deny'::character varying]::text[]))
);

